let contenedor = document.querySelector(".container");


const personCreator = (foto, nombre, email, id) => {
    let persona = document.createElement("div");
    let personaImagen = document.createElement("img");
    let personaText = document.createElement("p");
    personaText.innerHTML = `${nombre} <br /> ${email}`;
    personaImagen.src = foto;
    persona.id = id;
    persona.classList.add('person');
    persona.appendChild(personaImagen);
    persona.appendChild(personaText);
    contenedor.appendChild(persona);
    persona.addEventListener('click', mostrador, false)
}

const mostrador = (ev) => {
    personaDiv.style.display = '';
    contenedor.style.display = 'none';
    cargarPersona(data.results.filter(e => e.login.uuid === ev.currentTarget.id)[0]);
}

const lookUp = () => {
    contenedor.innerHTML = '';
    data.results
        .sort((a, b) => a.name.last.localeCompare(b.name.last))
        .map(e => personCreator(e.picture.large, `${e.name.title} ${e.name.first} ${e.name.last}`, e.email, e.login.uuid));
}

const lookUpZA = () => {
    contenedor.innerHTML = '';
    data.results
        .sort((a, b) => b.name.last.localeCompare(a.name.last))
        .map(e => personCreator(e.picture.large, `${e.name.title} ${e.name.first} ${e.name.last}`, e.email, e.login.uuid));
}

document.querySelector('#orden').addEventListener('click', (e) => {
    if (e.currentTarget.dataset.orden == 'true') {
        e.currentTarget.dataset.orden = 'false';
        lookUpZA()
    } else {
        e.currentTarget.dataset.orden = 'true';
        lookUp()
    }
})

lookUp();

const personFind = (term) => {
    contenedor.innerHTML = ''; //limpio el contenedor
    term = term.toLowerCase();

    const searchResult = data.results
        .filter(e => {
            let filtro = Object.values(e.name).map(a => a.toLowerCase());
            filtro.push(e.email);
            return filtro.includes(term)
        })
        .sort((a, b) => a.name.first.localeCompare(b.name.first))
        .map(e => personCreator(e.picture.large, `${e.name.title} ${e.name.first} ${e.name.last}`, e.email, e.login.uuid));
    if (searchResult.length === 0) contenedor.innerHTML = 'No hay resultados :C';
}

document.querySelector("#searchbox").addEventListener('keyup', (e) => {

    if (e.target.value.length == 0) lookUp();
    else personFind(e.target.value);
})


const personaDiv = document.querySelector('.personaBox');
const cargarPersona = (persona) => {
    let volver = document.createElement('button')
    volver.innerText = 'Volver atras';
    volver.addEventListener('click', () => {
        personaDiv.style.display = 'none';
        contenedor.style.display = '';
    })
    personaDiv.innerHTML = ``;
    Object.keys(persona).map(i => datosCreator(i, persona[i]))
    personaDiv.appendChild(volver);
}

const datosCreator = (dato, valor) => {
    let campo = document.createElement('p');
    if (typeof valor == 'object') valor = JSON.stringify(valor, undefined, 2);
    campo.innerHTML = `<strong>${dato}</strong>: ${valor}`
    personaDiv.appendChild(campo);
}